import React, { useState } from 'react';
import './SearchPage.css';

function SearchPage() {
  const [searchword, setSearchword] = useState('');

  const handleSearch: React.ChangeEventHandler<HTMLInputElement> = (event) => {
    setSearchword(event.target.value);
  };

  const searchArt = () => {
    fetch(
      `https://collectionapi.metmuseum.org/public/collection/v1/search?q=${searchword}`
        .then((response) => response.json())
        .then((data) => {})
    );
  };

  return (
    <div className="main-container">
      <div className="search-container d-flex">
        <h5 className="input-group-text">Search Art Piece</h5>
        <form className="input-form">
          <div className="input-group">
            <input
              type="text"
              aria-label="Artist"
              className="form-control"
              placeholder="sunflowers"
              value={searchword}
              onChange={handleSearch}
            />
            {/* <input type="text" aria-label="" className="form-control" /> */}
          </div>
        </form>
        <button type="submit" className="btn btn-primary" onClick={searchArt}>
          Search
        </button>
      </div>
      <div className="result-container">
        <div className="card mb-3">
          <div className="row">
            <div className="col-md-4">
              <img src="..." className="img-fluid rounded-start" alt="..." />
            </div>
            <div className="col-md-8">
              <div className="card-body">
                <h5 className="card-title">Title & Artist</h5>
                <p className="card-text">Description</p>
                <p className="card-text">
                  <small className="text-body-secondary">link</small>
                </p>
              </div>
            </div>
          </div>
        </div>
        <button type="button" className="btn add-btn btn-danger">
          Add to My Favorites
        </button>
      </div>
    </div>
  );
}

export default SearchPage;
