    )
      .then((response) => response.json())
      .then((data) => {
        // кладём массив в переменную состояния drinks
        setDrinks(data.drinks);
      });
  }, []);

  return (
    <div className="App">
      <nav className="navbar navbar-expand-lg navbar-light bg-white">
        <a className="navbar-brand" href="https://www.metmuseum.org/" />
        <img
          src="http://www.logologika.ru/i/upload/1736.jpg"
          className="img-fluid rounded-start w-8"
          alt="logo"
        />
        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className="navbar-nav ml-auto">
            <li className="nav-item mr-4" onClick={() => showSearch(page)}>
              <button className="nav-link mr-4">
                Search the Metropolitan Museum of Art
              </button>
            </li>
            <li className="nav-item">
              <button
                type="button"
                className="nav-link"
                onClick={() => showMyFavorites(page2)}
              >
                My Favorite Art Pieces
              </button>
            </li>
          </ul>
        </div>
      </nav>
      {/* <img width={400} src={logo} alt="react" /> */}
      <SearchPage />
      {/* <TweetsList /> */}
    </div>
  );
}

