import React, { useEffect, useState } from 'react';
import Card from '../../types/Card';
import './SearchPage.css';

function SearchPage() {
  const [searchword, setSearchWord] = useState('');
  const [searchResult, setSearchResult] = useState<number[]>([]);
  const [showCard, setShowCard] = useState<Card[]>([]);

  const handleSearch: React.ChangeEventHandler<HTMLInputElement> = (event) => {
    setSearchWord(event.target.value);
  };

  const searchArt = () => {
    fetch(
      `https://collectionapi.metmuseum.org/public/collection/v1/search?q=${searchword}`
    )
      .then((response) => response.json())
      .then((data) => {
        const items = data.objectIDs;
        for (let i = 0; i <= 10; i += 1) {
          let random = Math.floor(Math.random() * items.length);
          console.log(random);
        }
        setSearchResult(items);
      });
  };

  useEffect(() => {
    searchResult.forEach((el) =>
      fetch(
        `https://collectionapi.metmuseum.org/public/collection/v1/objects/${String(
          el
        )}`
      )
        .then((response) => response.json())
        .then((card) => {
          return setShowCard((prev) => [
            ...prev,
            {
              title: card.title,
              link: card.objectURL,
              artist: card.artistDisplayName,
              bio: card.artistDisplayBio,
              img: card.primaryImage,
              wiki: card.objectWikidata_URL,
            },
          ]);
        })
    );
  }, [searchResult]);

  //   Надо записать массив в реслокалс

  return (
    <div className="main-container">
      <div className="search-container d-flex">
        <h5 className="input-group-text">Search Art Piece</h5>
        <form className="input-form">
          <div className="input-group">
            <input
              type="text"
              aria-label="Artist"
              className="form-control ml-1"
              placeholder="Rembrandt"
              value={searchword}
              onChange={handleSearch}
            />
            {/* <input type="text" aria-label="" className="form-control" /> */}
          </div>
        </form>
        <button
          type="submit"
          className="btn btn-primary ml-1"
          onClick={searchArt}
        >
          Search
        </button>
      </div>
      {showCard.map((card) => (
        <div className="result-container">
          <div className="card">
            <div className="row">
              <div className="col-md-4">
                <img
                  src={card.img}
                  className="img-fluid rounded-start"
                  alt="pic"
                />
              </div>
              <div className="col-md-8">
                <div className="card-body">
                  <h5 className="card-title">
                    {card.artist} <br /> <br />
                    {card.title}
                  </h5>
                  <h6 className="card-title">{card.bio}</h6>
                  <p className="card-text">
                    <p>
                      <a href={card.link}>
                        Link to the Metropolitan Museum site
                      </a>
                    </p>
                    <p>
                      <a href={card.wiki}>Link to the Wikipedia page</a>
                    </p>
                  </p>
                </div>
              </div>
            </div>
          </div>
          <button type="button" className="btn add-btn btn-danger ">
            Add to My Favorites
          </button>
        </div>
      ))}
    </div>
  );
}

export default SearchPage;
